import "./index.css";
const AEurope = require("./components/AEurope");
const{ useState } = require("react");


function Piece({
    country,
    onClick,
    onMouseEnter,
    onMouseExit,
    selectedCountry,
    hoveredCountry
  }) {
    return (
      <svg
        width="231"
        height="121"
        viewBox="0 0 231 121"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          onClick={onClick}
          onMouseEnter={onMouseEnter}
          onMouseLeave={onMouseExit}
          className="shape"
          fill={selectedCountry === country ? "red" : "gray"}
          stroke={hoveredCountry === country ? "red" : "black"}
          d="M49 0.5L1 120L230 114L136 73L209.5 0.5H49Z"
        />
      </svg>
    );
  }
export default function App() {
    const [selectedCountry, setSelectedCountry] = useState("");
    const [hoveredCountry, setHoveredCountry] = useState("");

    const countries = [
      { name: "europe", pieces: [AEurope] }
    //   { name: "europe", pieces: [Piece, Piece, Piece] },
    //   { name: "northamerica", pieces: [Piece, Piece, Piece] }
    ];

    console.log(selectedCountry);

    return countries.map((country) => {
      const { name, pieces } = country;
      return pieces.map((Piece) => {
        return (
          <Piece
            country={name}
            selectedCountry={selectedCountry}
            hoveredCountry={hoveredCountry}
            onClick={() => setSelectedCountry(name)}
            onMouseEnter={() => setHoveredCountry(name)}
            onMouseExit={() => setHoveredCountry("")}
          />
        );
      });
    });
  }
